mapper.select:
SELECT 

FROM accountant_biz a

WHERE a.id = #{id}

SELECT 

FROM accountant_biz a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.user_id = #{user.id}


		AND a.company_id = #{companyId}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.show_hide = #{showHide}




		ORDER BY ${page.orderBy}

SELECT 

FROM accountant_biz a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}

mapper.update:
UPDATE accountant_biz SET 	
	name = #{name},
	sort = #{sort},
	remarks = #{remarks},
	show_hide = #{showHide}
WHERE id = #{id}

UPDATE accountant_biz SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
