mapper.select:
SELECT 

FROM accountant_source_doc_attachment a

WHERE a.id = #{id}

SELECT 

FROM accountant_source_doc_attachment a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.source_doc_id = #{sourceDocId}


		AND a.type = #{type}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

SELECT 

FROM accountant_source_doc_attachment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date ASC

mapper.update:
UPDATE accountant_source_doc_attachment SET 	
	source_doc_id = #{sourceDocId},
	files_path = #{filesPath},
	type = #{type},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE accountant_source_doc_attachment SET 
	del_flag = #{DEL_FLAG_DELETE}


		WHERE id = #{id}


		WHERE source_doc_id = #{sourceDocId}
